angular.module('main', [])
	// @param {string} amount - quantity of digits
	.filter('digitQuantity', function(){
		return function(number, amount){
			var quantity = amount - 0; // convert string to int
			return number.toFixed(quantity);
		}
	})
	.controller('table', ['$scope', function(scope){
		// scope is alias for '$scope',  model of controller
		this.title = 'Product prices';
		// variable of model that represent quantity of digits
		scope.digitNumber = '0';
		// table data
		scope.tableData = [
			{
				name: 'ice cream',
				price: 12.9856,
				amount: 3
			},
			{
				name: 'sandwitch avec poulet',
				price: 3.6782,
				amount: 2
			},
			{
				name: 'le poisson',
				price: 0.9999,
				amount: 0
			}
		];
	}]);

2.content:
html{ font: 13px/18px Arial; }	
body{ margin: 0; }
button, input{ font-family: inherit; }
table{ border-collapse: collapse; }

/* Helpers */
.clearfix:before, .clearfix:after { content: ""; display: table; }
.clearfix:after { clear: both; }

/*  Table */
.simple-table {
	width: 400px;
}
.simple-table th,
.simple-table td {
	padding: 3px 8px;
	border: 1px solid #ddd;
	text-align: left;
}
.simple-table th {
	text-transform: uppercase;
}

/* Header */ 
.simple-header {
	width: 100%;
	box-sizing: border-box;
	margin: 0 0 10px 0;
	padding: 10px 20px;
	background: #cfcfcf;

}
.simple-header_title { 
	font-size: 18px;
	line-height: 20px;
	text-transform: uppercase;
	font-weight: 400;
	margin: 0;
}
.simple-header_right-panel {
	float: right;
	line-height: 20px;
}
